import React from 'react';
import './person-details.css'
import SwapiService from '../../services/SwapiService.js'
class Persondetails extends React.Component {

  swapiInf  = new SwapiService();
  state = {
    person : null
  };

    componentDidMount(){
      this.updatePersone()
    }
  updatePersone =()=>{


    const{personId} = this.props;
    if(!personId){
      return ;
    }
    this.swapiInf.getPerson(personId)
      .then((person)=>{
        console.log(person)
        this.setState({
        person
        })

      })


  };

  render() {

const{name,id, eyecolor,BirthYear} = this.state;
console.log(this.state.person)



    return (
      <div className="preson-details ">
        <div className="row main-person">
          <div className='img-person'>
            <img src={`https://starwars-visualguide.com./assets/img/characters/${id}.jpg`} alt=""/>
          </div>
          <div className='about-person'>
            <h4>{name}{this.props.personId}</h4>
            <ul className='list-group-flush'>
              <li className='list-group-item'>
                <span>Birth Year :</span><span>{BirthYear}</span>
              </li>

              <li className='list-group-item'>
                <span>Gender :</span><span>{eyecolor}</span>
              </li>
              <li className='list-group-item'>
                <span>eyecolor :</span><span></span>
              </li>

            </ul>
          </div>
        </div>
      </div>
    )
  };
}
export default Persondetails
