import React from 'react';
import './person-details.css'
import SwapiService from '../../services/SwapiService.js'
class Persondetails extends React.Component {

  swapiInf  = new SwapiService();
  state = {
    person : null
  };
  constructor(){
    super();
    this.updatePersone();
}
  updatePersone =()=>{

    const{personId} = this.props;
    if(!personId){
      return ;
    }
    this.swapiInf.getPerson(personId)
      .then((person)=>{
        console.log(person)
        this.setState({
        person : person
        })

      })


  };

  render() {




    return (
      <div className="preson-details ">
        <div className="row main-person">
          <div className='img-person'>
            <img src="https://starwars-visualguide.com./assets/img/characters/3.jpg" alt=""/>
          </div>
          <div className='about-person'>
            <h4></h4>
            <ul className='list-group-flush'>
              <li className='list-group-item'>
                <span>Birth Year :</span><span></span>
              </li>

              <li className='list-group-item'>
                <span>Gender :</span><span></span>
              </li>
              <li className='list-group-item'>
                <span>Birth Year :</span><span>33BBYY</span>
              </li>

            </ul>
          </div>
        </div>
      </div>
    )
  };
}
export default Persondetails
