class SwapiService  {
  async getBody(url){
    const res = await fetch(url);
    if(!res.ok){
      throw new Error(res.status)
    }
    return await res.json()


  };
  getPeople(){
   return  this.getBody("https://swapi.co/api/people/1/")
  };
  async getStarShips(){
    const  res = await this.getBody("https://swapi.co/api/starships/");
    return res.results

  };
  getPlanets(){
    this.getBody("https://swapi.co/api/planets/")
  }
  async getPlanet(id){
    const planet = await this.getBody(`https://swapi.co/api/planets/${id}/`);
    return this._transformPlanet(planet)
  }
  getId(item){
    const idRegExp = /\/([0-9]*)\/$/;
    const id = item.url.match(idRegExp);
    console.log(id)
    return id
  }
  _transformPlanet(res){

    return{
      id:this.getId(res),
      name:res.name,
      population :res.population,
      Diameter:res.diameter,
      RotationPeriod:res.rotation_period
    }
  }

  _transformPeople(res){

    return{
      id:this.getId(res),
      name:res.name,
      population :res.population,
      Diameter:res.diameter,
      RotationPeriod:res.rotation_period
    }
  }
}
export default SwapiService
