import React from 'react';
import './random-planet.css'
import SwapiService from '../../services/SwapiService.js'



class Planets extends React.Component{


  swapiInf = new SwapiService();
  state ={
    name:null,
    population : null,
    RotationPeriod:null,
    Diameter : null


  };


  updatePlanet(){
    this.swapiInf.getPlanet(1)
      .then(res=>{
        this.setState({
          name:res.name,
          population :res.population,
          diameter:res.diameter
        })

    })
  }





  render() {
    this.updatePlanet()

    const{population,RotationPeriod,Diameter,name} = this.state;

    return (
      <div className="random-planet ">
        <div className="row main-planet">
          <div className='img-planet'>
            <img src="https://starwars-visualguide.com./assets/img/characters/3.jpg" alt=""/>
          </div>
          <div className='planet-person'>
            <h4>{name}</h4>
            <ul className='list-group-flush'>
              <li className='list-group-item'>
                <span>Population</span><span>{population
              }</span>
              </li>
              <li className='list-group-item'>
                <span>RotationPeriod :</span><span>{RotationPeriod}</span>
              </li>
              <li className='list-group-item'>
                <span>Diameter :</span><span>{Diameter}</span>
              </li>


            </ul>
          </div>
        </div>
      </div>
    )
  }
};
export default Planets
